# require 'pry'

PLUGINS_URL = 'https://github.com/evgenii/bitbar_trademan/blob/master/scripts'.freeze
CHECKS_DIR = "#{ENV['HOME']}/.bitbar_plugins/.trademan-plugins/".freeze
CONFIG_DIR = "#{ENV['HOME']}/.bitbar_plugins/.trademan/".freeze

DARK_MODE=`defaults read -g AppleInterfaceStyle 2> /dev/null`.strip
NO_DIM = "#{DARK_MODE == 'Dark' ? 'white' : 'black'} "

README_URL = "https://github.com/evgenii/bitbar_trademan/blob/master/readme.md".freeze

MAIN_SYMBOL = '¥'.freeze
UP_SYMBOL = '↑'
DOWN_SYMBOL = '↓'
UP_SYMBOL_BREAK_CEILING = '⇞'
DOWN_SYMBOL_BREAK_CEILING = '⇟'

# ICON_OK = "".freeze
# ICON_FAIL = "".freeze
ICON_UNDETERMINED = "".freeze

class Formatter
  attr_reader :data, :output

  # data structure example
  # data = {
  #   "block Title": [
  #     {
  #       status:                 - one of (nil means ok) :ok, :not_found, :connection_lost,
  #       iconImg: nil,           - string with BASE64 encoded image
  #       iconSym: '[√]',         - string with text to show
  #       itemHref: 'https://...' - link to process on click
  #       title: 'Some title',    -
  #       description: [          -
  #         'Message: value or something else'
  #       ]
  #     }
  #   ]
  # }
  def initialize(data)
    @data = data
    @output = ""
  end

  def to_bitbar
    _push_title(**prepare_bar_title)

    data.each do |title, blocks|
      _push_block(title, blocks) do |_, description|
        description.each { |txt| _push_subblock(txt) }
      end
    end
    _add_hotkeys(data[:hotkeys]) unless data[:hotkeys].nil? || data[:hotkeys].empty?
    _push_footer

    output
  end

  private

  def _push_footer
    _push_divider

    output << "Refresh... | refresh=true"
  end

  def _push_block(title, blocks)
    _push_divider
    _push_block_title(title)

    blocks.each do |block|
      next if block.nil? || block.size.zero?

      _push_title(sym: block[:itemText], img: block[:itemImg], href: block[:itemHref])
      yield(block[:title], block[:description]) if block.has_key?(:title) || block.has_key?(:description)
    end
  end

  def _push_subblock(txt, color: NO_DIM)
    output << "-- #{txt} | color=#{color}\n"
  end

  def _push_block_title(txt)
    "#{txt.strip}| size=12\n"
  end

  def _push_title(sym: nil, img: nil, href: nil, bash: {}, color: NO_DIM)
    raise ArgumentError, "None Symbol or Icon providing" if sym.nil? && img.nil?

    options = []
    options << ('image=' + img) if img
    options << ('href=' + href) if href
    options << bash.map { |k, c| "#{k}='#{c}'" }.join(' ') unless bash.empty?
    options << ('color=' + color) if color

    # line = '''osascript -e 'Tell application "System Events" to display dialog "How many minutes of freeing yourself from the world? or [hh:]mm:ss " default answer ""' -e 'text returned of result' 2>/dev/null '''
    # a_time = os.popen(line).read().strip()


    output << " #{sym} | #{options.join(' ')}\n"
  end

  def _add_hotkeys(hotkeys)
    _push_divider

    hotkeys.map do |name, cmd|
      _push_title(sym: name, bash: { bash: cmd, terminal: true, trim: false })
    end
  end

  def _push_divider
    output << "\n---\n"
  end

  def prepare_bar_title
    flatten_data = data.values.flatten
    flatten_data = [flatten_data] unless flatten_data.is_a?(Array)

    statuses = flatten_data.map { |i| i.fetch(:status, nil) }.uniq
    if statuses.include?(:connection_lost)
      { sym: '[!]', color: 'red' }
    else
      color = 0
      sym = [MAIN_SYMBOL] + flatten_data.map do |i|
        on_top = i.fetch(:on_top, nil)
        status = i.fetch(:status, nil)
        symbol = i.fetch(:symbol, nil)
        value = i.fetch(:value, nil) || i.fetch(:last_trade, nil)
        value = value.round(2) unless value.nil?

        if status == :grow
          color += 1
          symbol + (on_top ? UP_SYMBOL_BREAK_CEILING : UP_SYMBOL) + value.to_s
        elsif status == :fall
          color -= 1
          symbol + (on_top ? DOWN_SYMBOL_BREAK_CEILING : DOWN_SYMBOL) + value.to_s
        else
          symbol + value.to_s
        end
      end

      {
        sym: sym.join,
        color: color.zero? ? 'white' : (color > 0 ? 'green' : 'red')
      }
    end
  end
end

class TradeMan
  VERSION = 'v0.1.0'.freeze

  attr_reader :result

  def self.run
    self.new.preform
  end

  def initialize
    @result = Hash.new() { |hash, title| hash[title] = [] }
  end

  def preform
    return help unless any_configs? && any_availabe_scripts?

    require_scripts

    config_files.each do |config_file|
      process_config(config_file)
    end
    formatter = Formatter.new(result)
    puts formatter.to_bitbar
  end

  private

  #
  # config_file example:
  # {
  #   "BTC": [
  #     {
  #       "script": "exmo",
  #       "currency": "BTC-AUSD",
  #       "options": {
  #         "arg1": "val1"
  #       }
  #     }
  #   ]
  # }
  def process_config(config_file)
    settings = parse_config_file(config_file)
    settings.each do |title, scripts|
      scripts.each do |script_opts|
        data = proces_script(script_opts)

        if data.is_a?(Array)
          result[title] += data
        else
          result[title] << data
        end
      end
    end
  end

  def proces_script(script_opts)
    script_name = script_opts['script']
    return unless script_availabe?(script_name)

    run_script(script_name, script_opts['currency'], script_opts['options'])
  end

  #
  # script perform should return a Hash with data
  # @example:
  #   {
  #     status:                 - one of (nil means ok) :ok, :not_found, :connection_lost,
  #     iconImg: nil,           - string with BASE64 encoded image
  #     iconSym: '[√]',         - string with text to show
  #     itemHref: 'https://...' - link to process on click
  #     title: 'Some title',    -
  #     description: [          -
  #       'Message: value or something else'
  #     ]
  #   }
  #
  def run_script(name, currency, opts = {})
    inst = Object.const_get(camelize(name)).new(currency, opts)
    inst.preform
  end

  def script_availabe?(script_name)
    availabe_script_names.include?(script_name.to_s)
  end

  def availabe_scripts
    return @availabe_scripts if defined?(@availabe_scripts)

    FileUtils.mkdir_p(CHECKS_DIR)
    @availabe_scripts = Dir["#{checks_dir}/*"]
  end

  def require_scripts
    availabe_scripts.each do |scripts_path|
      require scripts_path
    end
  end

  def checks_dir
    return @checks_dir if defined?(@checks_dir)

    @checks_dir = dev_mode? ? './scripts' : CHECKS_DIR
  end

  def availabe_script_names
    return @availabe_script_names if defined?(@availabe_script_names)

    @availabe_script_names = availabe_scripts.map { |f_path| File.basename(f_path, ".*") }
  end

  def parse_config_file(config_file)
    JSON.parse(File.open(config_file).read)
  end

  # def plugin_path(plugin)
  #   FileUtils.mkdir_p(CHECKS_DIR)
  #   plugin_path = "#{CHECKS_DIR}/#{plugin}"
  #   return plugin_path if File.executable? plugin_path

  #   File.write(plugin_path, open("#{CHECKS_URL}/#{plugin}").read)
  #   File.chmod(0755, plugin_path)
  #   plugin_path
  # end

  def any_configs?
    config_files.count > 0
  end

  def any_availabe_scripts?
    availabe_scripts.count > 0
  end

  def config_files
    return @config_files if defined?(@config_files)
    config_dir = dev_mode? ? '.trademan' : CONFIG_DIR

    @check_files = Dir["#{config_dir}/*.json"]
  end

  def help
    if dev_mode?
      puts " [!] | "
    else
      puts " | image=#{ICON_UNDETERMINED}"
    end
    puts "---"
    puts "TradeMan #{VERSION}|href=#{README_URL}"
    puts "More info...|href=#{README_URL}"
    puts "---"
    puts "No configuration files found in #{CONFIG_DIR} | bash=/usr/bin/open param1=\"#{CONFIG_DIR}\" terminal=false"
    puts "Click here to learn how to write configuration files... | href=#{README_URL}"
    puts "---"
    puts "Refresh... | refresh=true"

    exit
  end

  def dev_mode?
    return @dev_mode if defined?(@dev_mode)

    @dev_mode = ENV['ENV'] == 'development'
  end

  def camelize(term, uppercase_first_letter = true)
    string = term.to_s
    if uppercase_first_letter
      string = string.sub(/^[a-z\d]*/) { |match| match.capitalize }
    else
      string = string.sub(inflections.acronyms_camelize_regex) { |match| match.downcase }
    end
    string.gsub!(/(?:_|(\/))([a-z\d]*)/) { "#{$1}#{$2.capitalize}" }
    string.gsub!("/".freeze, "::".freeze)
    string
  end
end

TradeMan.run
